import React ,{Component} from 'react'
import TodoItem from './TodoItem'
import axios from 'axios'


class TodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            inputValue:'hello',
            list:['一','二','三','四','五']
        }
    }

    componentDidMount(){
        axios.get('/api/todolist').then(()=>{})
    }


    handle提交(){
        console.log(this.state.inputValue)

        this.setState({
            list:[...this.state.list,this.state.inputValue],
            inputValue:''
        })
        
    }

    handleChange(e){
        console.log(e.target.value);
        this.setState(
            {
                inputValue:e.target.value
            }
        )
    }

    handelDelList= (index)=>{
        console.log('hand del list');
        console.log(index);
        const list = [...this.state.list]
        list.splice(index,1)
        this.setState(
            {
                list:list
            }
        )
    }

    render(){
        console.log('todo list render run');
        return(
            <div>
                <div>
                    <label htmlFor = '输入区域'>输入区域</label>
                    <input id='输入区域' value={this.state.inputValue}
                    onChange={(e)=>{this.handleChange(e)}}
                    />
                    <button onClick={()=>{this.handle提交()}}>提交</button>
                </div>
                <ul>
                   {
                       this.state.list.map(
                           (val,keyindex)=>{
                               return <TodoItem 
                               val = {val}
                               keyindex = {keyindex}
                               itemDel = {this.handelDelList}
                               ></TodoItem>
                           }
                       )
                   }
                </ul>

            </div>
        )
    }
}


export default TodoList
