import React, { Component } from 'react'
import 'antd/dist/antd.css';

import { Input, Button, List, Typography, Divider } from 'antd';
import store from './store/index'
import {CHANGE_INPUT_VALUE,ADD_TODO_ITEM,DELETE_TODO_ITEM} from './store/actionTypes'
import {getInputChangeAction}from ''

class TodoList extends Component {

    constructor(props){
        super(props)
        //console.log(store.getState());
       // console.log(this.getState);
        
        this.state ={ 
        };
        store.subscribe(this.handleStoreChange)
        
    }

    handleStoreChange=()=>{
        //console.log('handlStoreChange run ');
        this.setState(store.getState())
    }

    handleInputChage = (e)=>{
       // console.log('handleInputChage run:');
        //console.log(e.target.value);
        const action = {
            type:CHANGE_INPUT_VALUE,
            value:e.target.value
        }
        store.dispatch(action)
    }

    handleBtnClk=()=>{
        const action = {
            type:ADD_TODO_ITEM

        }
        store.dispatch(action)
    }

    handleDelClk=(index)=>{
        console.log('del clked ');
        alert(index)
        const action = {
            type:DELETE_TODO_ITEM,
            index:index
        }
        store.dispatch(action)
    }

    render() {
        return (
            <div style={{ marginTop: '10px', marginLeft: '10px' }}>
                <div> HELLO </div>
                <Input 
                value={this.state.inputValue} 
                placeholder="" 
                style={{ width: '300px' }}
                onChange={this.handleInputChage}
                 />
                <Button 
                type="primary"
                onClick={this.handleBtnClk}
                >Primary Button</Button>

                <Divider orientation="left">Default Size</Divider>
                <List
                    header={<div>Header</div>}
                    footer={<div>Footer</div>}
                    bordered
                    dataSource={this.state.list}
                    renderItem={(item,index) => (
                        <List.Item 
                        onClick = {this.handleDelClk.bind(this,index)}
                        >
                            <Typography.Text mark> list 

                            </Typography.Text> {item}
                        </List.Item>
                    )}
                />
            </div>
        )
    }
}
export default TodoList
